import React from 'react';

import Header from "./Header";
import Tasks from "./Tasks";
import AddTask from "./AddTask";

import App from '../App';

import "../App.css";


export default function Pagina1(){
    const [tasks, setTasks] = useState([
        {
          id: '1',
          title: 'Estudar Programação',
          completed: false,
        },
        {
          id: '2',
          title: 'Ler Livros',
          completed: true,
        },    
      ]);
    return(
        <>
        
      <div className="container">
        <Header />
		<>
			<AddTask handleTaskAddition={App.handleTaskAddition} />
            <Tasks
					tasks={tasks}
					handleTaskClick={App.handleTaskClick}
					handleTaskDeletion={App.handleTaskDeletion}
				/>
		</>        
      </div>
    </>
    );
}
